import React from "react";
import { Text, View } from "react-native";
import { TextInput } from "react-native-gesture-handler";
import { StyleSheet } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import * as firebase from 'firebase'

const firebaseConfig = {
  apiKey: "AIzaSyAiF7wXsuXDwXcUBWodKDTUlqV0vSHQkkQ",
  authDomain: "activate-76231.firebaseapp.com",
  projectId: "activate-76231",
  storageBucket: "activate-76231.appspot.com",
  messagingSenderId: "1052929519076",
  appId: "1:1052929519076:web:7b8772ee148757347dabf1",
  measurementId: "G-CHQ2BS7XMQ"
};
if (!firebase.apps.length) {
	firebase.initializeApp(firebaseConfig)
} else {
	firebase.app() // 이미 초기화되었다면, 초기화 된 것을 사용함
}


function setUserData(data){
  userData = data;
}

function getUserData(){
  return userData;
}
export {setUserData, getUserData};


export default class SignUp extends React.Component{
  state = {phoneNumber: '', password: ''};
  handleSignIp = () => {
    console.log(this.state);
  };

  render() {
    return (
      <View style={styles.container}>
          <View >
          <Text>회원가입 화면 입니다.</Text>
          {this.state.errorMessage && 
          <Text style={{color: 'red'}}>
          {this.state.errorMessage} </Text>}
          <TextInput style={styles.textInput} placeholder="Phone Number"
          onChangeText={phoneNumber => this.setState({phoneNumber})}
          value={this.state.phoneNumber}/>
          <TextInput style={styles.textInput} placeholder="password"
          onChangeText={password => this.setState({password})}
          value={this.state.password}/>
          <TextInput style={styles.textInput} placeholder="confrim pass"/>
          <TextInput style={styles.textInput} placeholder="Name"/>
          <TextInput style={styles.textInput} placeholder="Gender"/>
          <TextInput style={styles.textInput} placeholder="Birth Day"/>
          <TextInput style={styles.textInput} placeholder="보호자 번호"/>
          <TouchableOpacity style={styles.button} onPress={this.handleSignIp}>
            <Text>회원가입</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.button} onPress={() => this.props.navigation.navigate('Login')}>
            <Text>돌아가기</Text>
          </TouchableOpacity>
          </View>
      </View>);
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
      title: {
        fontSize: 50,
        color: 'blue'
      },
      paragraph: {
        fontSize: 30,
        color: 'black'
      },
      
      buttonText: {
        color: "white",
        fontSize: 30,
      },
      textInput: {
        height: 40,
        width: '90%',
        borderColor: 'gray',
        borderWidth: 1,
        marginTop: 8
      },
      button: {
        width: 200,
        height: 80,
        backgroundColor: "#123abc",
        borderRadius: 4.5,
        justifyContent: "center",
        alignItems: "center",
    },
  });
